-- ----------------------------
-- Table structure for craw_rule
-- ----------------------------
DROP TABLE IF EXISTS `craw_rule`;
CREATE TABLE `craw_rule` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) default NULL COMMENT '名称',
  `craw_store` varchar(255) default NULL COMMENT '唯一编号，作为存储表名',
  `craw_url` varchar(255) default NULL COMMENT '爬取路径',
  `craw_item` varchar(255) default NULL COMMENT '列表条目规则',
  `craw_next` varchar(255) default NULL,
  `list_ext` text COMMENT '扩展规则，json数组存储多个',
  `content_ext` text,
  `type` varchar(255) default NULL COMMENT '1列表 2详情',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8 COMMENT='jsoup爬虫解析规则';

-- ----------------------------
-- Records of craw_rule
-- ----------------------------

-- ----------------------------
-- Table structure for craw_store_gushi
-- ----------------------------
DROP TABLE IF EXISTS `craw_store_gushi`;
CREATE TABLE `craw_store_gushi` (
  `id` int(11) NOT NULL auto_increment COMMENT 'mysql自增主键',
  `title` varchar(255) default NULL COMMENT '标题',
  `url` varchar(255) default NULL COMMENT '详情地址，如果有详情页',
  `content` mediumtext COMMENT '详情内容',
  `status` int(11) NOT NULL default '1' COMMENT '状态：1未解析 2已解析 3解析失败',
  `create_time` datetime default NULL COMMENT '创建时间',
  `update_time` datetime default NULL COMMENT '修改时间',
  `danysty` varchar(500) default NULL COMMENT '朝代',
  `author` varchar(500) default NULL COMMENT '作者',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `url` (`url`)
) ENGINE=InnoDB AUTO_INCREMENT=111 DEFAULT CHARSET=utf8 COMMENT='古诗网';

-- ----------------------------
-- Records of craw_store_gushi
-- ----------------------------

-- ----------------------------
-- Table structure for craw_store_tianyi
-- ----------------------------
DROP TABLE IF EXISTS `craw_store_tianyi`;
CREATE TABLE `craw_store_tianyi` (
  `id` int(11) NOT NULL auto_increment COMMENT 'mysql自增主键',
  `title` varchar(255) default NULL COMMENT '标题',
  `url` varchar(255) default NULL COMMENT '详情地址，如果有详情页',
  `content` mediumtext COMMENT '详情内容',
  `status` int(11) NOT NULL default '1' COMMENT '状态：1未解析 2已解析 3解析失败',
  `create_time` datetime default NULL COMMENT '创建时间',
  `update_time` datetime default NULL COMMENT '修改时间',
  `cover` varchar(500) default NULL COMMENT '封面',
  `start_date` varchar(500) default NULL,
  `click_count` varchar(500) default NULL COMMENT '点击量',
  `dm_count` varchar(500) default NULL,
  `stow_count` varchar(500) default NULL,
  `v_ctimes` varchar(500) default NULL,
  `up_name` varchar(500) default NULL,
  `up_url` varchar(500) default NULL,
  `tags` varchar(500) default NULL COMMENT '标签',
  `embed_url` varchar(500) default NULL,
  PRIMARY KEY  (`id`),
  UNIQUE KEY `url` (`url`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='洛天依专题';

-- ----------------------------
-- Records of craw_store_tianyi
-- ----------------------------

-- ----------------------------
-- Table structure for sys_menu
-- ----------------------------
DROP TABLE IF EXISTS `sys_menu`;
CREATE TABLE `sys_menu` (
  `id` varchar(255) NOT NULL COMMENT '主键',
  `name` varchar(255) NOT NULL COMMENT '菜单名称',
  `url` varchar(255) NOT NULL COMMENT '菜单地址',
  `pid` varchar(255) default NULL COMMENT '上级id，0为根节点，最多三级菜单',
  `sort` int(11) default NULL COMMENT '排序',
  `icon` varchar(255) default NULL COMMENT '菜单图标',
  `type` varchar(255) default NULL COMMENT '类别',
  `status` int(11) default NULL COMMENT '状态',
  `create_time` datetime default NULL COMMENT '创建时间',
  `update_time` datetime default NULL COMMENT '更新时间',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统菜单表';

-- ----------------------------
-- Records of sys_menu
-- ----------------------------

-- ----------------------------
-- Table structure for sys_parameter
-- ----------------------------
DROP TABLE IF EXISTS `sys_parameter`;
CREATE TABLE `sys_parameter` (
  `id` varchar(255) NOT NULL COMMENT '主键',
  `name` varchar(255) NOT NULL COMMENT '参数名称',
  `param_key` varchar(255) NOT NULL COMMENT '英文键，唯一',
  `param_value` varchar(255) default NULL COMMENT '参数值',
  `remark` text COMMENT '备注',
  `create_time` datetime default NULL COMMENT '创建时间',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `index_param_key` (`param_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统参数表';

-- ----------------------------
-- Records of sys_parameter
-- ----------------------------

-- ----------------------------
-- Table structure for sys_role
-- ----------------------------
DROP TABLE IF EXISTS `sys_role`;
CREATE TABLE `sys_role` (
  `id` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
  `remark` varchar(255) default NULL,
  `create_time` datetime default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统角色表';

-- ----------------------------
-- Records of sys_role
-- ----------------------------

-- ----------------------------
-- Table structure for sys_role_menu
-- ----------------------------
DROP TABLE IF EXISTS `sys_role_menu`;
CREATE TABLE `sys_role_menu` (
  `id` varchar(255) NOT NULL default '' COMMENT '主键',
  `role_id` varchar(255) NOT NULL COMMENT '角色id',
  `menu_id` varchar(255) NOT NULL COMMENT '菜单Id',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统角色关联菜单表';

-- ----------------------------
-- Records of sys_role_menu
-- ----------------------------

-- ----------------------------
-- Table structure for sys_user
-- ----------------------------
DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user` (
  `id` varchar(255) NOT NULL COMMENT '主键',
  `username` varchar(255) NOT NULL COMMENT '用户名',
  `password` varchar(255) NOT NULL COMMENT '密码',
  `nickname` varchar(255) default NULL COMMENT '昵称',
  `gender` varchar(255) default NULL COMMENT '性别：1男 2女',
  `birthday` datetime default NULL COMMENT '生日',
  `avatar` varchar(255) default NULL COMMENT '头像',
  `remark` text COMMENT '备注',
  `status` int(11) default NULL COMMENT '状态',
  `last_login` datetime default NULL COMMENT '上次登录时间',
  `ip` varchar(255) default NULL COMMENT '登录ip',
  `auth_key` varchar(255) default NULL COMMENT '简单的浏览器自动登录cookie密钥，唯一',
  `create_time` datetime default NULL,
  `update_time` datetime default NULL,
  PRIMARY KEY  (`id`),
  UNIQUE KEY `index_username` USING BTREE (`username`),
  KEY `index_auth_key` USING BTREE (`auth_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统用户表';

-- ----------------------------
-- Records of sys_user
-- ----------------------------

-- ----------------------------
-- Table structure for sys_user_role
-- ----------------------------
DROP TABLE IF EXISTS `sys_user_role`;
CREATE TABLE `sys_user_role` (
  `id` varchar(255) NOT NULL default '',
  `user_id` varchar(255) NOT NULL COMMENT '用户Id',
  `role_id` varchar(255) NOT NULL COMMENT '角色id',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统用户关联角色表';

-- ----------------------------
-- Records of sys_user_role
-- ----------------------------
